commits.content:
@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700&display=swap");

:root {
    /* Colors */
    /* Primary */
    --red: hsl(0, 100%, 74%);
    --green: hsl(154, 59%, 51%);
    /* Accent */
    --blue: hsl(248, 32%, 49%);
    /* Neutral */
    --dark-blue: hsl(249, 10%, 26%);
    --grayish-blue: hsl(246, 25%, 77%);
    --white: #fff;
    /* Other variables */
    --font-size-base: 1rem; /* 16px */
    --font-weight-base: 400;
}

*,
*::after,
*::before {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body,
html {
    font-family: "Poppins", sans-serif;
    font-size: var(--font-size-base);
    font-weight: var(--font-weight-base);
    background: var(--red);
}

main {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    background-color: var(--red);
    background-image: url("./images/bg-intro-desktop.png");
    background-size: cover;
    background-position: center;
    width: 100%;
}

.container {
    min-height: 100vh;
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    padding: 5rem;
}

section {
    max-width: 100%;
    width: 50%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 2rem;
}

.learn {
    font-size: 3rem;
    font-weight: 700;
    color: #fff;
    line-height: 3.3rem;
    padding: 1rem;
}

.container-info p {
    color: var(--white);
    padding: 1rem;
    font-weight: 700;
}

.form {
    background-color: #fff;
    padding: 1.875rem;
    border-radius: 0.5rem;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 0.5rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

.price {
    width: 100%;
    font-size: 1rem;
    font-weight: 400;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: var(--blue);
    color: #fff;
    padding: 1.125rem;
    border-radius: 0.625rem;
    margin-bottom: 1.5rem;
}

.price span {
    font-weight: 700;
    margin-right: 0.25rem;
}

input[type="text"],
input[type="email"],
input[type="password"] {
    width: calc(100% - 1.125rem);
    padding: 1.125rem;
    margin-bottom: 1.200rem;
    border-radius: 5px;
    border: 1px solid #ccc;
    color: var(--dark-blue);
    font-size: 1rem;
    font-weight: 700;
    text-indent: 0.75rem;
}

input {
    border-style: none;
    outline: none;
}

input:focus {
    border: 1px solid var(--blue);
}

.input-field {
    border: 1px solid var(--dark-blue);
}

.input-field.input-error {
    border-color: var(--red);
}

.input-field.input-error::after {
    content: "";
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
    width: 10px;
    height: 10px;
    background-image: url(./images/icon-error.svg);
    background-size: contain;
    background-color: var(--red);
    border-radius: 50%;
}

.error-message {
    display: none;
    color: var(--red);
    font-size: 12px;
    margin-top: 5px;
}

.error-message.show {
    display: block;
}

button {
    background-color: var(--green);
    color: #fff;
    cursor: pointer;
    width: calc(100% - 1.25rem);
    padding: 1.125rem;
    margin-bottom: 1.200rem;
    border-radius: 5px;
    border: 1px solid #ccc;
    font-size: 1rem;
    font-weight: 700;
    text-transform: uppercase;
}

button:hover {
    background-color: hsl(154, 59%, 68%);
    cursor: pointer;
}

form p {
    color: var(--grayish-blue);
    font-size: 0.7rem;
    font-weight: 700;
    letter-spacing: -0.016rem;
    display: flex;
    align-items: center;
    justify-content: center;
}

.terms {
    color: var(--red);
    padding-left: 4px;
    font-weight: 700;
}

footer {
    margin-top: 20px;
    text-align: center;
}

.attribution {
    font-size: 11px;
    text-align: center;
}

.attribution a {
    color: hsl(228, 45%, 44%);
}

/* @media (max-width: 650px) {
    main {
        height: auto;
    }
    .container {
        width: 100%;
        flex-direction: column;
        padding: 1rem;
    }
    section {
        width: 100%;
        padding: 1rem;
    }

    .learn {
        font-size: 1.6rem;
        line-height: normal;
        margin: 3rem;
        padding: 1rem;
    }
} */

@media screen and (max-width: 768px) {
    .container {
        flex-direction: column;
        text-align: center;
        padding: 3rem;
    }

    section {
        width: 100%;
    }

    .container-form {
        margin-bottom: 5rem;
        margin-right: 0;
    }
}
