commits.content:
@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700&display=swap");

/* Variabile de culoare */
:root {
    /* Colors */
    /* Primary */
    --red: hsl(0, 100%, 74%);
    --green: hsl(154, 59%, 51%);
    /* Accent */
    --blue: hsl(248, 32%, 49%);
    /* Neutral */
    --dark-blue: hsl(249, 10%, 26%);
    --grayish-blue: hsl(246, 25%, 77%);
    --white: #fff;
}

/* Resetare stiluri și setări generale */
*,
*::after,
*::before {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body,
html {
    font-family: "Poppins", sans-serif;
    font-size: var(--font-size-base);
    font-weight: var(--font-weight-base);
    background-color: var(--red); /* Fundal roșu pentru body/main */
}
main {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    background-color: var(--red);
    background-image: url('./images/bg-intro-desktop.png');
    background-size: cover;
    background-position: center;
}

.container {
    display: flex;
    flex-direction: row;
    align-items: center;
    width: 90%;
    /* max-width: 800px; */
}

.info {
    padding: 20px;
    width: 50%;
}

.form {
    background-color: #fff; /* Fundal alb */
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.1); /* Umbră */
    width: 100%;
    max-width: 400px; /* Limitați lățimea formularului */
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}

h1 {
    font-size: 24px;
    font-weight: 600;
    color: #fff;
}

.info p {
    color: var(--grayish-blue);
}

h2 {
    width: 100%;
    font-size: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    background-color: var(--blue); /* Schimbați culoarea butonului aici */
    color: #fff; /* Text alb */
    padding: 10px 20px; /* Margini interioare pentru asemănare cu butonul */
    border-radius: 5px;
    margin-bottom: 20px; /* Spațiu între h2 și form */
}

p {
    margin-top: 10px;
}

input[type="text"],
input[type="email"],
input[type="password"],
input[type="button"] {
    width: calc(100% - 20px); /* Lățimea inputului minus paddingul */
    padding: 10px;
    margin-top: 16px;
    border-radius: 5px;
    border: 1px solid #ccc;
    color: var(--dark-blue);
}

input {
    border-style: none; /* Elimină stilul implicit al bordurii */
    outline: none;
}

input:focus {
    border: 1px solid var(--blue);
}

.input-field {
    border: 1px solid var(--dark-blue); /* Stilul borderului implicit */
    /* Alte stilizări */
}

.input-field.input-error {
    border-color: var(--red); /* Schimbarea culorii borderului la eroare */
}

.input-field.input-error::after {
    content: ''; /* Adăugarea cerculețului roșu */
    position: absolute;
    top: 50%;
    right: 10px;
    transform: translateY(-50%);
    width: 10px;
    height: 10px;
    background-image: url(./images/icon-error.svg);
    background-size: contain;
    background-color: var(--red);
    border-radius: 50%;
}

.error-message {
    display: none; /* Ascunderea mesajului de eroare inițial */
    color: var(--red);
    font-size: 12px;
    margin-top: 5px;
}

.error-message.show {
    display: block; /* Afișarea mesajului de eroare când este necesar */
}

input[type="button"] {
    background-color: var(--green); /* Schimbați culoarea butonului aici */
    color: #fff; /* Text alb */
    cursor: pointer;
    border: none;
}

input[type="button"]:hover {
    background-color: hsl(154, 59%, 68%);
    cursor: pointer;
}

form p {
    color: var(--grayish-blue);
    font-size: 0.5rem;
    display: flex;
    align-items: center;
    justify-content: center;
}

span {
    color: var(--red);
}

footer {
    margin-top: 20px;
    text-align: center;
}

.attribution {
    font-size: 11px;
    text-align: center;
}

.attribution a {
    color: hsl(228, 45%, 44%);
}
