commits.content:
@import url("https://fonts.googleapis.com/css2?family=Poppins:wght@400;500;600;700&display=swap");

:root {
    /* Colors */
    /* Primary */
    --red: hsl(0, 100%, 74%);
    --green: hsl(154, 59%, 51%);
    /* Accent */
    --blue: hsl(248, 32%, 49%);
    /* Neutral */
    --dark-blue: hsl(249, 10%, 26%);
    --grayish-blue: hsl(246, 25%, 77%);
    --white: #fff;
    /* Other variables */
    --font-size-base: 1rem; /* 16px */
    --font-weight-base: 400;
}

*,
*::after,
*::before {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body,
html {
    font-family: "Poppins", sans-serif;
    font-size: var(--font-size-base);
    font-weight: var(--font-weight-base);
}

.error-message,
.error-icon {
    display: none;
}

.field-group.error .error-message,
.field-group.error .error-icon {
    display: block;
    color: var(--red);
    font-size: 12px;
    margin-top: 5px;
}


main {
    min-height: 100vh;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    background: var(--red) url(/images/bg-intro-mobile.png) no-repeat;
    padding: 5.313rem 0;
}

.container {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    text-align: center;
    padding: 0 1rem;
    max-width: 1440px;
    max-height: 800px;
    margin: 0 auto;
}

.intro-container {
    flex: 1;
    margin: 1rem 0;
    padding: 0 1rem;
    color: var(--white);
}

.intro-container h1 {
    font-size: 1.7rem;
    line-height: 2.313rem;
    margin: 1rem;
}

.intro-container p {
    font-size: 1rem;
    line-height: 1.625rem;
    padding: 0.5rem 0;
}

.form-container {
    flex: 1;
    padding: 0 0.625rem;
    /* margin: 1rem 0; */
}

.form-container .top-box {
    background-color: var(--blue);
    color: var(--white);
    box-shadow: rgba(0, 0, 0, 0.1) 0px 0.5rem;
    padding: 0.313rem 10%;
    border-radius: 0.625rem;
    margin-bottom: 1.5rem;
}

.form-container .top-box p {
    padding: 1rem 2rem;
    font-size: 1rem;
    line-height: 1.5rem;
}

.form-container .top-box p span {
    font-weight: 700;
}

.form-container .form {
    background-color: var(--white);
    padding: 0 1.25rem;
    border-radius: 0.5rem;
    box-shadow: rgba(0, 0, 0, 0.1) 0px 0.5rem;
}

.form-container label {
    display: none;
}

.form-container input,
.form-container button[type="submit"] {
    width: 100%;
    height: 55px;
    border-radius: 0.25rem;
    border: 1px solid var(--grayish-blue);
    color: var(--dark-blue);
    padding-left: 1.25rem;
    outline: none;
    font-size: 0.936rem;
    font-weight: 600;
    caret-color: var(--blue);
}

.form-container input:focus {
    border: 1.5px solid var(--blue);
}

.form-container input::placeholder {
    color: var(--dark-blue);
    font-size: 0.936rem;
    font-weight: 600;
}

.form-container button[type="submit"] {
    border: none;
    background-color: var(--green);
    color: var(--white);
    cursor: pointer;
    box-shadow: 0px 4px hsl(154, 56%, 44%);
    font-size: 1rem;
    font-weight: 700;
    letter-spacing: 0.1px;
    text-transform: uppercase;
    margin-top: 0.75rem;
    margin-bottom: 0;
    padding: 1.313rem;
}

.form-container button[type="submit"]:hover {
    background-color: hsl(154, 80%, 63%);
}

.form-container .form-footer {
    padding: 0 1rem 1.5rem 1rem;
    font-size: 11px;
    margin-top: 10px;
    color: var(--grayish-blue);
    line-height: 1.25rem;
}

.form-container .form-footer a {
    color: var(--red);
    font-weight: 700;
}

.error-icon {
    position: absolute;
    top: 28%;
    right: 20px;
}

.error-message {
    text-align: right;
    font-size: 0.8rem;
    font-style: italic;
    color: var(--red);
    font-weight: 500;
}

footer {
    margin-top: 20px;
}

.attribution {
    font-size: 11px;
    text-align: center;
}

.attribution a {
    color: hsl(228, 45%, 44%);
}

@media only screen and (min-width: 1000px) {
    main {
        background-image: url(/images/bg-intro-desktop.png);
    }

    .container {
        flex-direction: row;
        padding: 1.5rem;
    }

    .intro-container {
        text-align: left;
        margin: 0 auto;
        padding: 8rem 0 5rem 8rem;
    }

    .intro-container h1 {
        font-size: 3rem;
        line-height: 3.5rem;
        margin-bottom: 1.75rem;
    }

    .form-container {
        padding: 0rem 3rem;
    }

    .field-group {
        padding: 0 1rem;
    }

    .form-container button[type="submit"] {
        width: 90%;
        border: none;
        background-color: var(--green);
        color: var(--white);
        cursor: pointer;
        box-shadow: 0px 4px hsl(154, 56%, 44%);
        font-size: 1rem;
        font-weight: 700;
        letter-spacing: 0.1px;
        text-transform: uppercase;
        margin-top: 1rem;
        margin-bottom: 0.5rem;
        padding: 1.5rem;
    }
}
