commits.content:
body {
    font-family: "Poppins", sans-serif;
}

#text-block {
    color: white;
    text-align: center;
}
#text-block__heading {
    font-size: 1.625rem;
    font-weight: 700;
    line-height: 1.4;
}
#text-block__description {
    line-height: 1.6;
}

#banner {
    font-weight: 300;
    color: white;
    text-align: center;
    line-height: 1.6;
}
#banner__text--bold {
    font-weight: 600;
}

#form input {
    font-size: 0.875rem;
    font-family: "Poppins", sans-serif;
    font-weight: 600;
    color: var(--dark-blue);
}
#form .form__error-message {
    font-size: 0.625rem;
    font-weight: 500;
    font-style: italic;
    color: var(--red);
    text-align: right;
}
#form__submit-button {
    font-size: 1rem;
    font-weight: 500;
    color: white;
}
#form #form__success-message {
    font-size: 0.875rem;
    font-weight: 500;
    color: var(--green);
    text-align: center;
}
#form__statement {
    font-size: 0.6875rem;
    color: var(--grayish-blue);
    line-height: 1.9;
    text-align: center;
}
#form__statement--highlighted {
    color: inherit;
    text-decoration: none;
    font-weight: 700;
    color: var(--red);
}

body {
    background: url("../images/bg-intro-mobile.png"), var(--red);
    background-size: 100%;
    background-attachment: fixed;
}

#banner {
    background: var(--blue);
    border-radius: 10px;
    box-shadow: 0 8px 0 rgba(0, 0, 0, 0.15);
}

#form {
    background: white;
    border-radius: 10px;
    box-shadow: 0 8px 0 rgba(0, 0, 0, 0.15);
}
#form input {
    border: 1px solid #dedede;
    border-radius: 5px;
}
#form__submit-button {
    background: var(--green);
    border: none;
    border-radius: 5px;
    box-shadow: inset 0 -4px 0 rgba(0, 0, 0, 0.1);
}

main {
    max-width: 500px;
    margin: 0 auto;
    padding: 5.75rem 24px;
}

#text-block {
    margin-bottom: 3.875rem;
}
#text-block__heading {
    margin-bottom: 1.375rem;
}

#banner {
    margin-bottom: 1.5rem;
    padding: 1.125rem 50px;
}

#form {
    padding: 24px;
}
#form .form__container {
    margin-bottom: 1rem;
    position: relative;
}
#form .form__container input {
    width: 100%;
    height: 3.5rem;
    padding: 0 20px;
}
#form .form__container .form__error-message {
    margin-top: 0.5rem;
}
#form .form__container .form__error-message--hidden {
    display: none;
}
#form .form__container .form__error-icon {
    width: 1.5rem;
    position: absolute;
    top: 1rem;
    right: 30px;
}
#form .form__container .form__error-icon--hidden {
    display: none;
}
#form__submit-button {
    width: 100%;
    margin-bottom: 0.75rem;
    padding: 0.9375rem 40px;
}
#form #form__success-message {
    margin-bottom: 1rem;
}
#form .form__success-message--hidden {
    display: none;
}
#form__statement {
    padding: 0 20px;
}

@media screen and (min-width: 700px) {
    body {
        background: url("../images/bg-intro-desktop.png"), var(--red);
        background-size: 100%;
        background-attachment: fixed;
    }
}
@media screen and (min-width: 1440px) {
    #text-block {
        text-align: start;
    }
    #text-block__heading {
        font-size: 3.125rem;
        line-height: 1.1;
    }
    #text-block__description {
        line-height: 1.7;
    }
    main {
        display: flex;
        justify-content: center;
        align-items: center;
        margin: 0;
        padding: 0;
        max-width: none;
        height: 100vh;
    }
    #text-block {
        width: 500px;
        margin: 0 70px 0 0;
    }
    #text-block__heading {
        margin-bottom: 1.625rem;
    }
    #banner {
        margin-bottom: 1.375rem;
    }
    #form-container {
        width: 540px;
    }
    #form {
        padding: 40px;
    }
    #form .form__container {
        margin-bottom: 1.25rem;
    }
    #form .form__container input {
        padding: 0 30px;
    }
    #form__submit-button {
        margin-bottom: 0.875rem;
    }
}
:root {
    --red: hsl(0, 100%, 74%);
    --green: hsl(154, 59%, 51%);
    --blue: hsl(248, 32%, 49%);
    --dark-blue: hsl(249, 10%, 26%);
    --grayish-blue: hsl(246, 25%, 77%);
}

#form input:focus-visible {
    border: 1px solid #605692;
    outline: none;
}

#form .form__input-field--invalid {
    color: var(--red);
    border: 2px solid var(--red);
}

#form .form__input-field--invalid::-moz-placeholder {
    color: var(--red);
}

#form .form__input-field--invalid:-ms-input-placeholder {
    color: var(--red);
}

#form .form__input-field--invalid::placeholder {
    color: var(--red);
}

#form .form__input-field--invalid:focus-visible {
    border: 2px solid var(--red);
}

#form__submit-button:hover {
    cursor: pointer;
    background: #77e2b4;
}

#form__submit-button:focus-visible {
    background: #77e2b4;
    outline: 2px solid black;
}

#form__statement--highlighted:hover,
#form__statement--highlighted:focus {
    text-decoration: underline;
}

#form__statement--highlighted:active {
    color: #e66767;
} /*# sourceMappingURL=style.css.map */
